import React from 'react';

const Form = ({ catName, setCatName }) => {
  const categoryNameHandler = (e) => {
    setCatName(e.target.value);
  };

  return (
    <div className='form'>
      <label>
        צבע רקע
        <input type='text' name='name' />
      </label>
      <label>
        שם קטגוריה
        <input type='text' name='categoryName' onChange={categoryNameHandler} />
      </label>
    </div>
  );
};

export default Form;
